mapper.select:
SELECT	COUNT(*)

FROM	SMC.SMC_ALERTS

WHERE	ALERT_ID != #{alertId}
AND		TEMPLATE_ID = #{templateId}

mapper.update:
UPDATE 	SMC.SMC_ALERT_HEADER

SET		HEADER_NAME = #{headerName},
		HELP_TEXT = #{helpText},
		DISPLAY_SEQUENCE = #{displaySequence}
		
WHERE	HEADER_ID = #{alertId}

UPDATE SMC.SMC_ALERTS
SET ALERT_NAME = #{alertName}, TEMPLATE_ID = #{templateID}, HELP_TEXT = #{helpText}, DISPLAY_SEQUENCE = #{displaySequence}, VISIBILITY_PENSKE = #{visibilityPenske}, VISIBILITY_VENDOR = #{visibilityVendor}
WHERE ALERT_ID = #{alertId}
