mapper.select:
SELECT v.OrderId, v.DeliveryId, v.LocationNumber, v.DeliveryDate as deliveryDate, v.Region, v.Area, v.District, v.DistrictDesc, v.AddCount, v.ReplaceCount, v.PackageName, CASE WHEN v.REARDOORINSTALLED = 'YES' then 1 ELSE 0 END AS hasReardoorUnits, CASE WHEN v.LIFTGATEINSTALLED = 'YES' then 1 ELSE 0 END AS hasLiftgateUnits, CASE WHEN v.REEFERINSTALLED = 'YES' then 1 ELSE 0 END AS hasReeferUnits, o.ApprovalStatus FROM v_ApprovedOrdersForBuildMatrix v JOIN t_Order o ON o.OrderId = v.OrderId JOIN (VALUES
(#{orderKey.orderId}, #{orderKey.deliveryId})
) AS keys (orderId, deliveryId) ON v.orderId = keys.orderId AND v.deliveryId = keys.deliveryId
