mapper.select:
SELECT v.OrderId, v.DeliveryId, v.LocationNumber, v.DeliveryDate as deliveryDate, v.Region, v.Area, v.District, v.DistrictDesc, v.AddCount, v.ReplaceCount, v.PackageName, o.ApprovalStatus FROM v_ApprovedOrdersForBuildMatrix v JOIN t_Order o ON o.OrderId = v.OrderId JOIN (VALUES
(#{orderKey.orderId}, #{orderKey.deliveryId})
) AS keys (orderId, deliveryId) ON v.orderId = keys.orderId AND v.deliveryId = keys.deliveryId
