mapper.select:
SELECT   OFM.DISTRICT AS districtValues
FROM     SMC.OBM_FREIGHT_MILEAGE OFM
WHERE    OFM.PLANT_ID = #{plantId}
AND      OFM.AREA = #{area}

mapper.update:
MERGE INTO SMC.OBM_PLANT_PROXIMITY AS proximity USING ( VALUES
( CAST(#{data.plantId} AS INTEGER), CAST(#{data.tier} AS INTEGER), CAST(#{data.district} AS VARCHAR(10)) )
) AS input ( PLANT_ID,TIER,DISTRICT ) ON proximity.PLANT_ID=input.PLANT_ID AND proximity.TIER=input.TIER AND proximity.DISTRICT=input.DISTRICT WHEN MATCHED THEN DELETE WHEN NOT MATCHED THEN INSERT ( PLANT_ID, TIER, DISTRICT ) VALUES ( input.PLANT_ID, input.TIER, input.DISTRICT )

MERGE INTO SMC.OBM_PLANTS_OFFLINE_DATES AS offlineDates USING ( VALUES
( CAST(#{offlineDate.offlineDateId} AS INTEGER), CAST(#{offlineDate.plantId} AS INTEGER), CAST(#{offlineDate.offlineStartDate} AS DATE), CAST(#{offlineDate.offlineEndDate} AS DATE) )
) AS input ( OFFLINE_DATE_ID,PLANT_ID,OFFLINE_START,OFFLINE_END ) ON offlineDates.OFFLINE_DATE_ID=input.OFFLINE_DATE_ID WHEN MATCHED THEN UPDATE SET offlineDates.OFFLINE_START= input.OFFLINE_START , offlineDates.OFFLINE_END= input.OFFLINE_END WHEN NOT MATCHED THEN INSERT ( PLANT_ID, OFFLINE_START, OFFLINE_END ) VALUES ( input.PLANT_ID, input.OFFLINE_START, input.OFFLINE_END )

mapper.insert:
INSERT INTO SMC.OBM_CRO_BUILD_REQUEST (
	RUN_ID,
	ORDER_ID,
	DELIVERY_ID
) VALUES (
	#{buildId},
	#{order.orderId},
	#{order.deliveryId}
)

mapper.delete:
DELETE FROM SMC.OBM_UNITS_EXCLUDED_FROM_BUILD
WHERE RUN_YEAR = #{year}
AND UNIT_NUMBER IN (
	
		#{unit}
	
)

mapper.update:
UPDATE SMC.OBM_BUSINESS_AWARD_DEFAULTS
SET 
	ITEM_ORDER = #{awardDefault.itemOrder},
	DEFAULT_VALUE = #{awardDefault.defaultPercentage}
WHERE
	ITEM_ID = #{awardDefault.attributeValueId}

mapper.delete:
		DELETE
		FROM 	OBM_ATTRIBUTE_VALUE
		WHERE 	ATTRIBUTE_ID=#{attributeId}
		 
           	AND 	ATTRIBUTE_VALUE_ID NOT IN 
		   	#{id}

mapper.select:
 	SELECT   TRIM(ATTRIBUTE_VALUE)
	FROM     SMC.OBM_ATTRIBUTE_VALUE
	WHERE    ATTRIBUTE_ID=#{attributeId}
