mapper.update:
UPDATE 	SMC.SMC_PO_CATEGORY
SET		STATUS = #{status}
WHERE	PO_CATEGORY_ID = #{poCatId}

UPDATE 	SMC.SMC_PO_CATEGORY_ASSOC 
SET 	ACTIVE =  #{status} 
WHERE 	PO_CATEGORY_ID = #{poCatId}

UPDATE 	SMC.SMC_PO_SUBCATEGORY
SET		STATUS = #{status}
WHERE	PO_SUBCATEGORY_ID = #{subCatId}

UPDATE 	SMC.SMC_PO_CATEGORY_ASSOC 
SET 	ACTIVE = #{status} 
WHERE 	PO_SUBCATEGORY_ID = #{subCatId}

UPDATE 	SMC.SMC_PO_CATEGORY_ASSOC 
SET 	ACTIVE = #{status} 
WHERE 	PO_CATEGORY_ASSOC_ID = #{assId}
