mapper.select:
SELECT GLOBAL_EXCEPTION_ID as exceptionId, DATA_ID as dataId, DATA_TYPE as dataType, TO_BE_PROVIDED_BY_PO as providerPo, TO_BE_PROVIDED_BY_PO_SUB as providerPoSub, CREATED_BY as createdByName, PO_GROUP as poGroup
FROM SMC.SMC_GLOBAL_EXCEPTIONS
WHERE GLOBAL_EXCEPTION_ID = #{id}

SELECT	COMPONENT_NAME

FROM	SMC.SMC_COMPONENT_INFO

WHERE	COMPONENT_ID = #{dataId}

SELECT	DESCRIPTION

FROM	SMC.SMC_VEHICLE_INFO

WHERE	VEHICLE_INFO_ID = #{dataId}

SELECT	FIRST_NAME

FROM	SMC.SMC_USER_MASTER

WHERE	USER_ID = #{id}

SELECT	LAST_NAME

FROM	SMC.SMC_USER_MASTER

WHERE	USER_ID = #{id}

mapper.update:
UPDATE	SMC.SMC_GLOBAL_EXCEPTIONS

SET		TO_BE_PROVIDED_BY_PO = #{provider},
		TO_BE_PROVIDED_BY_PO_SUB = #{subProvider}
		
WHERE	GLOBAL_EXCEPTION_ID = #{id}

UPDATE	SMC.SMC_UNIT_EXCEPTIONS

SET		TO_BE_PROVIDED_BY_PO = #{provider},
		TO_BE_PROVIDED_BY_PO_SUB = #{subProvider}
		
WHERE	UNIT_EXCEPTION_ID = #{id}

mapper.select:
SELECT UNIT_EXCEPTION_ID as exceptionId, DATA_ID as dataId, DATA_TYPE as dataType, UNIT_NUMBER as unitNumber, TO_BE_PROVIDED_BY_PO as providerPo, TO_BE_PROVIDED_BY_PO_SUB as providerSubPo, CREATED_BY as creatorId, PO_GROUP as poGroup
FROM SMC.SMC_UNIT_EXCEPTIONS
WHERE UNIT_EXCEPTION_ID = #{id}
