mapper.update:
UPDATE SMC_CMP_VISIBILITY_RULES_MASTER
NAME = #{rule.ruleName}, DESCRIPTION = #{rule.description}, MODIFIED_BY = #{user.userSSO}, MODIFIED_DATE = CURRENT_TIMESTAMP
WHERE RULE_ID = #{rule.ruleId}

UPDATE SMC_CMP_VISIBILITY_RULES_DEFINITION
CRITERIA_GROUP = #{ruleDef.criteriaGroup}, COMPONENT_ID = #{ruleDef.componentId}, COMPONENT_TYPE = #{ruleDef.componentType}, OPERAND = #{ruleDef.operand}, COMPONENT_VALUE = #{ruleDef.value}, MODIFIED_BY = #{user.userSSO}, MODIFIED_DATE = CURRENT_TIMESTAMP
WHERE RULE_DEF_ID = #{ruleDef.ruleDefId} AND RULE_ID = #{ruleDef.ruleId}

mapper.select:
SELECT SEQ_MASTER_ID AS id, NAME AS name, DESCRIPTION AS description, PO_CATEGORY AS category, TYPE AS type, LSM.OEM AS oem, FIRST_NAME||' '||LAST_NAME AS editedBy, LSM.MODIFIED_DATE AS editedDate FROM SMC_LOADSHEET_SEQ_MASTER LSM JOIN SMC_USER_MASTER SUM ON LSM.MODIFIED_BY=SUM.SSO
LSM.PO_CATEGORY=#{category}
AND LSM.TYPE=#{type}

SELECT DISTINCT VCS.COMPONENT_ID AS componentId, TRIM(VCS.COMPONENT_GROUP) AS componentGroup, TRIM(VCS.SUB_GROUP) AS subGroup, TRIM(VCS.SUB_COMPONENT_NAME) AS componentName FROM SMC_COMPONENT_VISIBILITY SCV JOIN CORP.VEHCMPSGF VCS ON SCV.COMPONENT_ID=VCS.COMPONENT_ID JOIN SMC_CAT_TYPE_MASTER SCM ON SCV.CAT_TYPE_ID=SCM.CAT_TYPE_ID
SCV.LOADSHEET_VISIBILITY !=''
AND SCM.PO_CATEGORY = #{category}
AND SCM.TYPE = 'DEFAULT'
AND SCM.TYPE = #{type}
Order By componentGroup,subGroup,componentName

mapper.update:
UPDATE SMC_LOADSHEET_SEQ_MASTER
NAME = #{seqMaster.name}, DESCRIPTION = #{seqMaster.description}, OEM = #{seqMaster.oem}, MODIFIED_BY = #{user.userSSO}, MODIFIED_DATE = CURRENT_TIMESTAMP
WHERE SEQ_MASTER_ID = #{seqMaster.id}

UPDATE SMC_LOADSHEET_GROUP_MASTER
NAME = #{grpMaster.name}, DISPLAY_SEQ = #{grpMaster.displaySeq}, MODIFIED_BY = #{user.userSSO}, MODIFIED_DATE = CURRENT_TIMESTAMP
WHERE GRP_MASTER_ID = #{grpMaster.grpMasterId}

mapper.delete:
DELETE FROM SMC_LOADSHEET_GROUP_MASTER WHERE GRP_MASTER_ID NOT IN
#{grpMasterId}
AND SEQ_MASTER_ID = #{seqMasterId}

mapper.update:
UPDATE SMC_LOADSHEET_CMP_GROUP_SEQ
DISPLAY_SEQ = #{cmpGrpSeq.displaySeq}, MODIFIED_BY = #{user.userSSO}, MODIFIED_DATE = CURRENT_TIMESTAMP
WHERE GRP_MASTER_ID = #{cmpGrpSeq.grpMasterId} AND CMP_GRP_SEQ_ID = #{cmpGrpSeq.compGrpSeqId} AND COMPONENT_ID = #{cmpGrpSeq.componentId}

mapper.delete:
DELETE FROM SMC_LOADSHEET_CMP_GROUP_SEQ WHERE CMP_GRP_SEQ_ID NOT IN
#{compGrpSeqId}
AND GRP_MASTER_ID = #{grpMasterId}

mapper.select:
 		SELECT 
				count(*)
		FROM
				SMC_LOADSHEET_SEQ_MASTER
		WHERE
		   PO_CATEGORY=#{category} 
		
				 AND TYPE=#{type}
		
		
				AND OEM=#{mfr}
		
		
				AND SEQ_MASTER_ID NOT IN ( #{sequenceId} )
