mapper.select:
SELECT DISTINCT excp.GLOBAL_EXCEPTION_ID as exceptionId, excp.NEW_EXCEPTION as isNew, comps.DISPLAY_NAME as componentName, excp.UNIT_SIGNATURE as unitSignature,DATE(excp.MODIFIED_DATE) as modifiedDate, excp.COMPONENT_ID,excp.PROVIDER_PO_CATEGORY_ASSOC_ID,excp.PROVIDER_VENDOR_ID, POC.PO_CATEGORY ||'-'|| SUBC.PO_SUBCATEGORY as providerPo, (SELECT SUBSTR(xmlserialize(xmlagg(xmltext(CONCAT( ', ',POC.PO_CATEGORY ||'-'|| SUBC.PO_SUBCATEGORY))) as VARCHAR(1024)), 3) FROM SMC.SMC_UNIT_SIGNATURE_DEFINITION USD JOIN SMC.SMC_PO_CATEGORY_ASSOC asoc ON USD.PO_CATEGORY_ASSOC_ID = asoc.PO_CATEGORY_ASSOC_ID LEFT JOIN SMC.SMC_PO_CATEGORY POC ON POC.PO_CATEGORY_ID = asoc.PO_CATEGORY_ID LEFT JOIN SMC.SMC_PO_SUBCATEGORY SUBC ON SUBC.PO_SUBCATEGORY_ID = asoc.PO_SUBCATEGORY_ID WHERE USD.UNIT_SIGNATURE=excp.UNIT_SIGNATURE ) as POGROUP, excp.PROVIDER_VENDOR_ID, excp.PROVIDER_VENDOR_ID AS VENDOR_ID FROM SMC.SMC_GLOBAL_EXCEPTIONS excp JOIN SMC.SMC_COMPONENT_INFO_DETAILS as comps ON comps.COMPONENT_ID= excp.COMPONENT_ID JOIN SMC.SMC_PO_CATEGORY_ASSOC asoc ON excp.PROVIDER_PO_CATEGORY_ASSOC_ID = asoc.PO_CATEGORY_ASSOC_ID LEFT JOIN SMC.SMC_PO_CATEGORY POC ON POC.PO_CATEGORY_ID = asoc.PO_CATEGORY_ID LEFT JOIN SMC.SMC_PO_SUBCATEGORY SUBC ON SUBC.PO_SUBCATEGORY_ID = asoc.PO_SUBCATEGORY_ID LEFT JOIN SMC.SMC_UNIT_MASTER UM ON UM.UNIT_SIGNATURE = excp.UNIT_SIGNATURE LEFT JOIN SMC.SMC_UNIT_COMPONENT SUC ON SUC.COMPONENT_ID = excp.COMPONENT_ID AND SUC.CONFLICT_STATUS='G' AND UM.MASTER_ID=SUC.MASTER_ID
excp.GLOBAL_EXCEPTION_ID = #{exceptionId}
AND UM.UNIT_NUMBER = #{unitNumber}
AND UM.PO_NUMBER = #{poNumber}
ORDER BY modifiedDate desc
