mapper.select:
SELECT 	sso 
FROM 	SMC.SMC_USER_MASTER 
WHERE 	UPPER(SSO) = #{userName}
		
		AND USER_ID != #{userId}

SELECT 	SIGN_FILE 
FROM 	SMC.SMC_USER_INITIALS
WHERE 	USER_ID = #{value}

SELECT 	INIT_FILE
FROM 	SMC.SMC_USER_INITIALS
WHERE 	USER_ID = #{value}

	SELECT USER_ID as userId,INIT_FILE as initString,
			SIGN_FILE as signString 
 	FROM SMC.SMC_USER_INITIALS WHERE USER_ID= #{value}

mapper.delete:
 		DELETE 	FROM SMC.SMC_ORG_MASTER 
 		WHERE ORG_ID IN(
			SELECT ORG_ID FROM SMC.SMC_ORG_MASTER 
			START  WITH ORG_ID =#{value}
		 	CONNECT BY PARENT_ORG_ID = PRIOR ORG_ID ORDER BY ORG_ID
		)

mapper.select:
SELECT m.VENDOR_ID, m.VENDOR_NUMBER, m.CORP, m.VENDOR_NAME, purv.pvscty as city, purv.pvsst as state, purv.pvszip as zipCode FROM SMC.SMC_ORG_VENDOR_ASSOC as asso JOIN SMC.SMC_VENDOR_MASTER m ON asso.VENDOR_ID=m.VENDOR_ID INNER JOIN corp.fpurvnd purv on purv.pvcorp = m.corp and purv.pvnbr = m.vendor_number
CORP=#{corp}
AND UPPER(VENDOR_NAME) like #{vendor}
AND asso.ORG_ID=#{orgId}
AND m.STATUS = 'A'
GROUP BY m.VENDOR_ID, m.VENDOR_NAME, m.corp, m.VENDOR_NUMBER, purv.pvscty, purv.pvsst, purv.pvszip order by VENDOR_NAME ,VENDOR_NUMBER

mapper.delete:
 		DELETE FROM SMC.SMC_ORG_VENDOR_ASSOC WHERE ORG_ID IN(
			SELECT ORG_ID FROM SMC.SMC_ORG_MASTER 
			START  WITH ORG_ID =#{orgId}
		 	CONNECT BY PARENT_ORG_ID = PRIOR ORG_ID ORDER BY ORG_ID
		)

mapper.select:
 		SELECT 	USER_DEPT AS userDept,DESCRIPTION as description,
 				DEPT_ID AS userDeptId 
		FROM 	SMC.SMC_USER_DEPT where DEPT_ID=#{deptId}

	SELECT ORG_ID as orgId, NAME as orgName, PARENT_ORG_ID as parentOrgId FROM SMC.SMC_ORG_MASTER 
	START  WITH PARENT_ORG_ID =#{orgId}
 	CONNECT BY PARENT_ORG_ID = PRIOR ORG_ID ORDER BY ORG_ID

WITH result (Org_id , name, parent_org_id,description) AS ( SELECT Org_id , name, parent_org_id,description from SMC.SMC_ORG_MASTER WHERE Org_id = #{orgId} UNION ALL SELECT a.Org_id,a.name,a.parent_org_id,a.description FROM SMC.SMC_ORG_MASTER a INNER JOIN result s ON a.parent_org_id = s.Org_id) SELECT r.Org_id as orgId, r.name as orgName, r.description as orgDescription,(select name from SMC.SMC_ORG_MASTER where org_id=r.parent_org_id) as parentorgname,r.parent_org_id as parentOrgId FROM result r
r.name=#{orgName}
AND r.parent_org_id=#{parentOrgId}

	SELECT ORG_ID as orgId, NAME as orgName, PARENT_ORG_ID as parentOrgId FROM SMC.SMC_ORG_MASTER 
	START  WITH ORG_ID =#{orgId}
 	CONNECT BY PARENT_ORG_ID = PRIOR ORG_ID ORDER BY ORG_ID

mapper.delete:
 		DELETE FROM SMC.SMC_ORG_VENDOR_ASSOC 
			WHERE 
		      VENDOR_ID NOT IN(
		   	       SELECT   VENDOR_ID FROM     SMC.SMC_ORG_VENDOR_ASSOC
		               WHERE    ORG_ID=#{orgId}
	          )
			AND
			ORG_ID IN(
						SELECT ORG_ID FROM SMC.SMC_ORG_MASTER 
		                     WHERE ORG_ID != #{orgId} 
							 START WITH ORG_ID = #{orgId}
							 CONNECT BY PARENT_ORG_ID = PRIOR ORG_ID
					)

DELETE FROM SMC.SMC_ORG_VENDOR_ASSOC 
WHERE ORG_ID = #{orgId}
AND VENDOR_ID IN

	#{vendorNumber}
